import axios from 'axios';
import react, { useState } from 'react';

import './App.css';




function App() {
  const [message, setMessage] = useState([{id: 0, message: ""}]);
  const [response, setResponse] = useState("");
  var [count, setCount] = useState(1);
  var [user, setUser] = useState([]);
  var [holder,setHolder] = useState("");
  var [grabber,setGrabber] = useState("");
  var [test, setTest] = useState("");



  /*
  const [question, setQuestion] = useState("");
  const [reply, setReply] = useState("");
  const [resList, setResList] = useState([]);
  const [counter, setCounter] = useState(0);
  */
  /*
  const addQuestion = () => {
    console.log(question)
    axios.post('http://localhost:5001/create', {question: question})
    .then( () => { 
        console.log("success");
    });
  }
  */
  function toTitleCase(str) {
    return str.toLowerCase().split(' ').map(function (word) {
      return (word.charAt(0).toUpperCase() + word.slice(1));
    }).join(' ');
  }  


  const getReply = () => {
    console.log("what")
    axios.get('http://137.184.50.129:50101/response')
    //axios.get('http://localhost:8000/response')
    .then( (response) => {
      console.log("success")
      console.log(response.data[0]["original_text"])
      let data = toTitleCase(response.data[0]["original_text"])
      console.log(data)
      setResponse(data)
    }).catch(err => {
      console.log(err)
    })
  }

  const addElement = (e) => {
    setHolder(e.target.value)
  }

  const pushElement = () => {
    const hold = {holder}
    var newList = user.concat(hold)
    /*
    var newList = []
    if (count % 2 === 0){
      newList = user.concat([hold,"left"])
    }
    else{
      newList = user.concat([hold,"right"])
    }
    */
    setCount(count + 1)
    setUser(newList)
  }

  const showList = () => {
    user.map((e) => {
      return <h1>{e}</h1>})


  }

  const grab = (e) => {
    setGrabber(e)
    console.log("grabberSet")
  }






  return(

  <div className="background">
      <container class="title">
        <div className="titleDiv">
          <h1>Math Word Problem Tutor</h1>
        </div>
      </container>
      <container class="second">
          <div className="input1">
              <h2>Sample MWP</h2>
              <form>
                  <input type="text" onChange={(e) => {
                    setHolder(e.target.value)
                    setTest(e.target.value)


                    }}></input>
              </form>
              <button className="button1" type="Submit" onClick={pushElement}>Sumbit Question</button>

          </div>
          <div className="res">
            <div className="messages">
              {user.map((e) => {
                return <div className="temps"><div className="messageBox"><p>{e.holder}</p></div></div>
              })}
            </div>
              <h3 className="response">{response}</h3>
          </div>
          <div className="resButton">
              <button onClick={getReply}>
                Get Response
              </button>

          </div>
         <div className="messages"> 
          {user.map((e) => {
            /*
            if (e === "right"){
              console.log("mapright")

              return <div className="right"><div><h1>{grabber}</h1><h2>check</h2></div></div>
            }
            else if(e === "left"){
              console.log("mapleft")

              return <div className="left"><div><h1>{grabber}</h1><h2>check</h2></div></div>
            }
            else{
              console.log("btwn")

              return <div><h6>inbtwn</h6></div>
            }
            */
           return <div className="temps"><div className="messageBox"><p>{e.holder}</p></div></div>
          })}
         </div>
      </container>
  </div>
  )
//Maybe try using a for loop in the return instead of map
//return <h1>{e.holder}</h1>})}
/*{user.map((e) => {
  if (e === "right"){
    console.log("mapright")
    console.log(e)
    return <div className="right"><h1>{e.holder}</h1></div>
  }
  else{
    console.log("mapleft")
    console.log(e)
    return <div className="left"><h1>{e.holder}</h1></div>
  }
  })}
  */
}

export default App;
