import axios from 'axios';
import react, { useState } from 'react';

import './App.css';


function App() {
  const [response, setResponse] = useState("");
  var [count, setCount] = useState(1);
  var [user, setUser] = useState([]);
  var [holder,setHolder] = useState("");
  var [grabber,setGrabber] = useState("");
  var [test, setTest] = useState("");
  var [answers, setAnswers] = useState([]);
  var [ifwrong, setIfwrong] = useState(false);
  var [wrongIndex, setWrongindex] = useState(0);
  var [saveQuestion, setSavequestion] = useState("");



  function toTitleCase(str) {
    return str.toLowerCase().split(' ').map(function (word) {
      return (word.charAt(0).toUpperCase() + ' ' + word.slice(1));
    }).join(' ');
  }  


  const getReply = () => {
    console.log("helpme")
    axios.get('http://localhost:8000/response') //normal port is http://137.184.50.129:50101/response
    //axios.get('http://localhost:8000/response')
    .then( (response) => {
      console.log("success")


      let data = response.data["context"]
      data = data + response.data["question"]
      var answer = response.data["answer"]
      console.log("bfPush")
      pushAns(answer)
      dbPush(data)
      setResponse(response.data)

    }).catch(err => {
      console.log("inreserr")
      console.log(err)
    })
  }

  const postReply = (newlist) => {
    console.log("helpme")
    axios.get('http://localhost:8000/response') //normal port is http://137.184.50.129:50101/response
    //axios.get('http://localhost:8000/response')
    .then( (response) => {
      console.log("success")


      let data = response.data["context"]
      data = "Correct! \n" + data + response.data["question"]
      var answer = response.data["answer"]
      console.log("bfPush")
      pushAns(answer)
      newlist = newlist.concat({"holder": data})
      console.log(newlist)
      setUser(newlist)
      setResponse(response.data)

    }).catch(err => {
      console.log("inreserr")
      console.log(err)
    })
  }

  const pushAns = (answer) => {

    var newList = answers.concat(answer)
    console.log("here")
    console.log(newList)
    setAnswers(newList)
    console.log(answers)
  }

  const dbPush = (data) => {
    let obj = [{"holder": data}]
    //var newList = user.concat(obj)
    setCount(count + 1)
    //setUser(newList)
    setUser(obj)
    console.log("db")
    //console.log(newList)
    console.log(user)

  }

  const addElement = (e) => {
    setHolder(e.target.value)
  }

  const pushElement = () => {
    const hold = {holder}

    var newlist = user.concat({"holder": hold.holder})
    console.log(newlist)

    checkAnswer(newlist)
  }

  const checkAnswer = (newlist) => {
    console.log("incheck")
    console.log(newlist)
    console.log(answers[answers.length - 1])
    console.log(newlist[newlist.length - 1].holder)


    if (answers[answers.length - 1] == newlist[newlist.length - 1].holder){
      console.log("correct")

      if (ifwrong){

        if (wrongIndex == response["potential_context"].length - 1){

          setWrongindex(0)
          var buf = "Correct!\n" + response["question"]
          var newList = newlist.concat({"holder": buf})
          setUser(newList)
          setAnswers(answers.concat(response["answer"]))
          setIfwrong(false)
        }

        else{

          var buf = "Correct!\n" + response["potential_context"][wrongIndex + 1]["question"]
          var newList = newlist.concat({"holder": buf})
          setWrongindex(wrongIndex + 1)
          setUser(newList)
          setAnswers(answers.concat(response["potential_context"][wrongIndex + 1]["answer"]))

        }


      }
      else{
        postReply(newlist)
      }

    }
    else{
      console.log("wrong")
      if ( ifwrong ){
        var inter = "incorrect \n" + response["potential_context"][wrongIndex]["question"] 
        setUser(newlist.concat({"holder": inter}))
      }
      else{


        setIfwrong(true)

        setSavequestion(user[user.length - 1].holder)

        var intermediate =  "incorrect \n" + response["potential_context"][wrongIndex]["question"]

        newList = newlist.concat({"holder": intermediate})
        setUser(newList)
        console.log(response["potential_context"][wrongIndex]["answer"])
        setAnswers(answers.concat(response["potential_context"][wrongIndex]["answer"]))



      }
    }
    setCount(count + 1)
  }





  const showList = () => {
    user.map((e) => {
      return <h1>{e}</h1>})


  }

  const grab = (e) => {
    setGrabber(e)
    console.log("grabberSet")
  }


  return(

  <div className="background">
      <container class="title">
        <div className="titleDiv">
          <h1>Math Word Problem Tutor</h1>
        </div>
      </container>
      <container class="second">
          <div className="input1">
              <h1>Sample MWP</h1>
              <form>
                  <input className="responseField" type="text" onChange={(e) => {
                    setHolder(e.target.value)
                    setTest(e.target.value)


                    }}></input>
              </form>
              <button className="button1" type="Submit" onClick={pushElement}>Sumbit</button>

          </div>
          <div className="res">
            <div className="messages">
              {user.map((e) => {
                return <div className="temps"><div className="messageBox"><p>{e.holder}</p></div></div>
              })}
            </div>
          </div>
          <div className="resButton">
              <button onClick={getReply}>
                Get Response
              </button>

          </div>
      </container>
  </div>
  )

}

export default App;
