import axios from 'axios';
import react, { useState } from 'react';

import './App.css';




function App() {
  const [message, setMessage] = useState([{id: 0, message: ""}]);
  const [response, setResponse] = useState("");
  var [count, setCount] = useState(1);
  var [user, setUser] = useState([]);
  var [holder,setHolder] = useState("");
  var [grabber,setGrabber] = useState("");
  var [test, setTest] = useState("");
  var [answers, setAnswers] = useState([]);
  var [status, Setstatus] = useState("");



  /*
  const [question, setQuestion] = useState("");
  const [reply, setReply] = useState("");
  const [resList, setResList] = useState([]);
  const [counter, setCounter] = useState(0);
  */
  /*
  const addQuestion = () => {
    console.log(question)
    axios.post('http://localhost:5001/create', {question: question})
    .then( () => { 
        console.log("success");
    });
  }
  */

  function toTitleCase(str) {
    return str.toLowerCase().split(' ').map(function (word) {
      return (word.charAt(0).toUpperCase() + word.slice(1));
    }).join(' ');
  }  


  const getReply = () => {
    console.log("helpme")
    axios.get('http://localhost:8000/response') //normal port is http://137.184.50.129:50101/response
    //axios.get('http://localhost:8000/response')
    .then( (response) => {
      console.log("success")
      console.log(response.data[0]["original_text"])
      let data = toTitleCase(response.data[0]["original_text"])
      var answer = response.data[0]["ans"]
      pushAns(answer)
      dbPush(data)
    }).catch(err => {
      console.log(err)
    })
  }

  const pushAns = (answer) => {
    let obj = {"ans": answer}
    var newList = answers.concat(obj)
    console.log("here")
    console.log(newList)
    setAnswers(newList)
    console.log(answers)
  }

  const dbPush = (data) => {
    let obj = {"holder": data}
    var newList = user.concat(obj)
    setCount(count + 1)
    setUser(newList)
    console.log("db")
    console.log(newList)
    console.log(user)

  }

  const addElement = (e) => {
    setHolder(e.target.value)
  }

  const pushElement = () => {
    const hold = {holder}
    var newList = user.concat(hold)


    setCount(count + 1)
    setUser(newList)
    console.log(newList)
  }

  /*
  const checkAnswer = () => {
    var correct = {answers}
    var correctAns = correct["ans"]
    if (correctAns == user[user.length - 1]){

    }

  }
  */

  const showList = () => {
    user.map((e) => {
      return <h1>{e}</h1>})


  }

  const grab = (e) => {
    setGrabber(e)
    console.log("grabberSet")
  }






  return(

  <div className="background">
      <container class="title">
        <div className="titleDiv">
          <h1>Math Word Problem Tutor</h1>
        </div>
      </container>
      <container class="second">
          <div className="input1">
              <h1>Sample MWP</h1>
              <form>
                  <input type="text" onChange={(e) => {
                    setHolder(e.target.value)
                    setTest(e.target.value)


                    }}></input>
              </form>
              <button className="button1" type="Submit" onClick={pushElement}>Sumbit Question</button>

          </div>
          <div className="res">
            <div className="messages">
              {user.map((e) => {
                return <div className="temps"><div className="messageBox"><p>{e.holder}</p></div></div>
              })}
            </div>
          </div>
          <div className="resButton">
              <button onClick={getReply}>
                Get Response
              </button>

          </div>
      </container>
  </div>
  )


