import './App.css';
import axios from 'axios';
import react, { useState, useEffect } from 'react';

function App() {

  const [question, setQuestion] = useState("");
  const [reply, setReply] = useState("");
  const [resList, setResList] = useState([]);
  const [counter, setCounter] = useState(0);

  const addQuestion = () => {
    console.log(question)
    axios.post('http://localhost:5000/question', {question: question})
    .then( () => { 
        console.log("success");
    });
  }

  const getReply = () => {
    console.log(reply)
    axios.get('http://localhost:5000/reply')
    .then( (response) => {
      console.log("success")
      setReply(response.data)
    });
  }

  return (
    <div className="App">
      <header name="header">
        <h5 name="extra">Notre Dame CSE Department</h5>
      </header>
      <container class="title">
        <h1>Math Word Problem Tutor</h1>
      </container>
      <container class="second">
        <div className="input1">
          <h2>Sample MWP</h2>
          <form>
            <input type="text" value={question} onChange={(e) => {setQuestion(e.target.value)}}></input>
          </form>
          <button type="Submit" onClick={() => {
            addQuestion()
            setCounter(counter+1)
          }}>Sumbit Question</button>
        </div>
        <div className="sub">
          <button type="Submit" onClick={() => {
            addQuestion()
            setCounter(counter+1)
          }}>Sumbit Question</button>
        </div>
        <div className="res">
          <h1>Tutor:  </h1>
          <h1>{reply}</h1>
        </div>
      </container>
    </div>
  );
}

export default App;
