  const [message, setMessage] = useState([{id: 0, message: ""}]);
  const [response, setResponse] = useState("");
  useEffect( () => 
    onSnapshot(collection(db,"nd-cse-mwpResearch"),(snapshot) => 
    setMessage(snapshot.docs.map((doc) => ({ ...doc.data(), id: doc.id })))
