import './App.css';
import axios from 'axios';
import react, { useState, useEffect } from 'react';
import db from './firebase';
import { onSnapshot, collection } from 'firebase/firestore';




function App() {
  const [message, setMessage] = useState([]);
  useEffect( () => 
    onSnapshot(collection(db,"nd-cse-mwpResearch"),(snapshot) => {
      setMessage(snapshot.docs.map((doc) => doc.data()))
    }),
   []);
  /*
  const [question, setQuestion] = useState("");
  const [reply, setReply] = useState("");
  const [resList, setResList] = useState([]);
  const [counter, setCounter] = useState(0);
  */
  /*
  const addQuestion = () => {
    console.log(question)
    axios.post('http://localhost:5001/create', {question: question})
    .then( () => { 
        console.log("success");
    });
  }
  */
  /*
  const getReply = () => {
    console.log(reply)
    axios.get('http://localhost:5000/reply')
    .then( (response) => {
      console.log("success")
      setReply(response.data)
    });
  }
  */

  return(

  <div>
      <container class="title">
          <h1>Math Word Problem Tutor</h1>
      </container>
      <container class="second">
          <div className="input1">
              <h2>Sample MWP</h2>
              <form>
                  <input type="text" value={message} onChange={(e) => {setMessage(e.target.value)}}></input>
              </form>
              <button className="button1" type="Submit">Sumbit Question</button>
          </div>
          <div className="res">
              <h1>Tutor:  </h1>
          </div>
          <div className="resButton">
              <button>Get Response</button>
              {message.map((mes) => {
                <h1>{mes.id}</h1>
              })}
              {message.map((mes) => {
                <h1>{mes.message}</h1>
              })} 
          </div>
      </container>
  </div>
  )

}




export default App;
